import React, {Component} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import {HashRouter as Router, Route} from 'react-router-dom';

class Signup extends Component {

  constructor(props){
    super(props);
    this.state = {
      name: '',
      email: '',
      password: '',
    }
  }

  componentDidMount(){
    console.log('we are in chewie');
  }

  onChangeEmail = (e) => {
    this.setState({email: e.target.value})
  }
  onChangeName = (e) => {
    this.setState({name: e.target.value})
  }
  onChangePassword = (e) => {
    this.setState({password: e.target.value})
  }

  handleSubmit = (e) => {
    e.preventDefault();
    console.log(this.state.name);
    // request to server here
    // axios.post('/', {
    axios.post('http://localhost:4000/users', {
      username: this.state.email,
      password: this.state.password
    })
        .then(response => {
          console.log(response);
          if (response.data) {
            console.log('successful signup');
            this.setState({
              redirectTo: '/login'
            })
          } else {
            console.log('Sign-up error');
          }
          }).catch(error => {
            console.log('Signup Server Error');
            console.log(error);
          })
  }

  render(){
    return(
      <div>
        <h3>Sign up</h3>
        <form onSubmit={this.handleSubmit}>
          <input type="text" placeholder="Name" onChange={this.onChangeName} />
          <br />
          <input type="text" placeholder="email" onChange={this.onChangeEmail} />
          <br />
          <input type="text" placeholder="password" onChange={this.onChangePassword}/>
          <br />
          <input type="submit" />
        </form>
      </div>
    )
  }

}

export default Signup;
